-- Simple LET
EXECUTE IMMEDIATE
BEGIN
LET CUR_TABLE CURSOR FOR SELECT * FROM THE_TABLE;
END;
-- Simple FOR
EXECUTE IMMEDIATE
BEGIN
FOR THE_ROW IN CUR_TABLE DO
    LET NEW_VAR := THE_ROW.COL1 || "_SUFFIX";
END FOR;
END;
-- FOR and LET in LOOP
EXECUTE IMMEDIATE
BEGIN
LET CUR_TABLE CURSOR FOR SELECT * FROM THE_TABLE;
FOR THE_ROW IN CUR_TABLE DO
    LET NEW_VAR := THE_ROW.COL1 || "_SUFFIX";
END FOR;
END;
